    private void enviarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_enviarActionPerformed
       
         String nom,tel,cor,fech,pass,respuesta;
        
         
        nom=n.getText();
        tel=t.getText(); 
        cor=c.getText();
        fech=f.getText();
        pass=p.getText(); 
       
        
        respuesta = registroUser(nom,tel ,cor ,fech ,pass );
        
        txtrespuesta.setText(respuesta);
        
        login l1 = new login();
        l1.setVisible(true);
        setVisible(false);
    }//GEN-LAST:event_enviarActionPerformed

