    private static String registroUser(java.lang.String nombre, java.lang.String telefono, java.lang.String correo, java.lang.String fecha, java.lang.String password) {
        WS.RegistroUsuario_Service service = new WS.RegistroUsuario_Service();
        WS.RegistroUsuario port = service.getRegistroUsuarioPort();
        return port.registroUser(nombre, telefono, correo, fecha, password);
    }
