    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        
        
        String origen,destino,fecha,tipo,num,respu;
        
         
        origen=ori.getText();
        destino=des.getText(); 
        fecha=fec.getText();
         tipo=tip.getSelectedItem().toString(); 
        num=no.getText();
        
         respu = compraVuelo(origen, destino, fecha, num, tipo);
        
        txtrespu.setText(respu);
        
        CompraBoleto c1=new CompraBoleto();
        c1.setVisible(true);
        setVisible(false);
        
        
    }//GEN-LAST:event_jButton1ActionPerformed

