    private static String compraVuelo(java.lang.String origen, java.lang.String destino, java.lang.String fecha, java.lang.String personas, java.lang.String tipo) {
        WS.CompraVuelo_Service service = new WS.CompraVuelo_Service();
        WS.CompraVuelo port = service.getCompraVueloPort();
        return port.compraVuelo(origen, destino, fecha, personas, tipo);
    }
